mapper.update:
UPDATE MEMBER
SET MEMBER_ROLL = #{memberRoll}
WHERE MEMBER_ID = #{memberId}

UPDATE MEMBER
SET MEMBER_NAME = #{memberName}
    , MEMBER_TEL = #{memberTel}
    , MEMBER_EMAIL = #{memberEmail}
    , POST_CODE = #{postCode}
    , MEMBER_ADDR = #{memberAddr}
    , ADDR_DETAIL = #{addrDetail}
WHERE MEMBER_ID = #{memberId}

mapper.insert:
INSERT INTO CLASS_INFO (
    CLASS_NAME
    , CLASS_PAYMENT
    , CLASS_PERSONNEL
    , CLASS_SDATE
    , CLASS_EDATE
    , TEACHER_NUM
) VALUES (
    #{className}
    , #{classPayment}
    , #{classPersonnel}
    , #{classSdate}
    , #{classEdate}
    , #{teacherNum}
)

mapper.update:
UPDATE TEACHER
SET TEACHER_WORK = #{teacherWork}
WHERE TEACHER_NUM = #{teacherNum}

UPDATE CLASS_INFO
SET CLASS_NAME =#{className}
    , CLASS_PAYMENT = #{classPayment}
    , CLASS_PERSONNEL = #{classPersonnel}
    , CLASS_SDATE = #{classSdate}
    , CLASS_EDATE = #{classEdate}
    , TEACHER_NUM = #{teacherNum}
WHERE CLASS_NUM = #{classNum}

mapper.insert:
INSERT INTO operator(
    OPER_PAY
    , PAY_YEAR
    , PAY_MONTH
    , MEMBER_ID
    , CLASS_NUM
) VALUES (
    #{classPayment}
    , (SELECT REPLACE(FORMAT(YEAR(CURDATE()), 0), ',', ''))
    , (SELECT MONTH(CURDATE()))
    , #{memberId}
    , #{classNum}
)
