mapper.select:
SELECT BOARD_NUM , BOARD_TITLE , BOARD_CONTENT , B.MEMBER_ID , TYPE_NUM , BOARD_CNT , MEMBER_ROLL FROM BOARD B INNER JOIN MEMBER M ON B.MEMBER_ID = M.MEMBER_ID
WHERE ${searchType} LIKE CONCAT('%', #{searchValue}, '%')
ORDER BY BOARD_NUM DESC LIMIT ${displayDataCnt} OFFSET ${displayDataCnt * (nowPage - 1)}

mapper.insert:
INSERT INTO BOARD (
