mapper.update:
UPDATE BOARD
SET
    BOARD_TITLE = #{boardTitle}
    , BOARD_CONTENT = #{boardContent}
WHERE BOARD_NUM = #{boardNum}

mapper.select:
SELECT BOARD_NUM
        , BOARD_TITLE
FROM BOARD
WHERE BOARD_NUM > #{currentBoardNum}
ORDER BY BOARD_NUM ASC
LIMIT 1;

