mapper.select:
SELECT M.MEMBER_ID , MEMBER_NAME , MEMBER_BIRTH , MEMBER_ROLL , LEFT(REG_DATE, 16) REG_DATE , CASE WHEN MEMBER_ROLL = 1 THEN '학생' WHEN MEMBER_ROLL = 2 THEN '강사' WHEN MEMBER_ROLL = 3 THEN '관리' END AS STR_ROLL , OPER_NUM , IS_PAY FROM MEMBER M LEFT JOIN OPERATOR O ON M.MEMBER_ID = O.MEMBER_ID
WHERE MEMBER_ROLL = ${searchType}
ORDER BY MEMBER_NAME

mapper.insert:
INSERT INTO MEMBER(
    MEMBER_ID
    , MEMBER_PW
    , MEMBER_NAME
    , MEMBER_TEL
    , MEMBER_EMAIL
    , MEMBER_BIRTH
    , MEMBER_ADDR
    , ADDR_DETAIL
    , MEMBER_GENDER
    , POST_CODE
)VALUES(
    #{memberId}
    , #{memberPw}
    , #{memberName}
    , #{memberTel}
    , #{memberEmail}
    , #{memberBirth}
    , #{memberAddr}
    , #{addrDetail}
    , #{memberGender}
    , #{postCode}
)

mapper.select:
SELECT MEMBER_ID FROM member
WHERE MEMBER_NAME = #{memberName}
AND MEMBER_TEL = #{memberTel}

SELECT MEMBER_EMAIL FROM member
WHERE MEMBER_ID = #{memberId}
AND MEMBER_NAME = #{memberName}

mapper.update:
UPDATE MEMBER SET
MEMBER_PW = #{memberPw}
WHERE MEMBER_ID = #{memberId}
